    /**
     * @event show 
     */
    function doShow(UXWindowEvent $e = null)
    {
        $file = "data.txt";
        
        if(Stream::exists($file)) {
           $this->playerNameField->text = Stream::getContents($file);
        }
    }
    /**
     * @event buttonSave.click-Left 
     */
    function doButtonSaveClickLeft(UXMouseEvent $e = null)
    {
        Stream::putContents("data.txt", $this->playerNameField->text);        
    }
    

